function _callEventListeners(that, type, evt)
{
    DEBUG && (function() {
        if (type === "managerdebugevent")
        {
            _trace("managerdebugevent:", evt.message);
        }
    })();

    try
    {
        that.callEventListeners(type, evt);
    }
    catch(err)
    {
        that._warn("caught error in listener, evt: " + JSON.stringify(evt) +
                   ", err: " + err);
    }
}

